#include "Smith_Waterman.h"
#include "Alignment.h"
#include "Result.h"

#pragma warning(disable:4018)



/// <summary>
/// Entry point
/// </summary>
int main(int argc, char* argv)
{
    ScoringMatrix matrix;
    
    auto score = Smith_Waterman(
        "", 
        "", 
        &matrix).Align(10, 1).m_score;

    cout << "Score = " << score << endl;

    return 0;
}
